import React from "react";

function Form () {
    const [name, setName] = React.useState("");
    const [password, setPassword] = React.useState({value:"", isTouched: false});
    const [comment, setComment] = React.useState("");
    const getIsFormValid = () => {
        return (
            name && comment && password.value.length >= 8
            );
    };

    const clearForm = () => { 
        setName(""); 
        setComment(""); 
        setPassword({ 
          value: "", 
          isTouched: false, 
        }); 
      }; 

    const PasswordErrorMessage = () => {
        return (
            <p className="FieldError">Password should have at least 8 characters</p> 
        );
    };

    const handleSubmit = (event) => {
        event.preventDefault();
        alert("created")
        clearForm();
    }
    return (
        <div>
        <form className="" id="" onSubmit={handleSubmit}>
            <h1>Any question? Send us a message!</h1>
            <fieldset>
                <div>
                <label>Name:</label>
                <input type="text" placeholder="name" value={name} onChange={(e) => setName(e.target.value)}></input>
                </div>
                <div>
                <label>Password:</label>
                <input type="password" placeholder="1234" 
                value={password.value} 
                onChange={(e) => setPassword({...password, value: e.target.value})} 
                onBlur={() => { setPassword({ ...password, isTouched: true }); }}/>
                {password.isTouched && password.value.length < 8 ? (<PasswordErrorMessage/>):null}
                </div>
                <div>
                <label>Comment:</label>
                <textarea type="text" placeholder="Leave your comment..." value={comment} onChange={(e) => setComment(e.target.value)}></textarea>
                </div>
                <button type="submit" disabled={!getIsFormValid()}>Submit</button>
            </fieldset>
        </form>
        </div>
    )

};

  export default Form;
