import React from "react";
import {useState} from "react"; 
import {validateEmail} from "../components/EmailValidation"; 
import '../assets/../css/Contact.css';
import {
  FormControl,
  FormLabel,
  Input,
  FormErrorMessage,
  FormHelperText,
} from '@chakra-ui/react'

function Form () {
    const [name, setName] = React.useState("");
    const [email, setEmail] = useState("");
    const [comment, setComment] = React.useState("");


    const clearForm = () => { 
        setName(""); 
        setComment(""); 
        setEmail(""); 
      }; 

      const getIsFormValid = () => { 
        return ( 
          name && 
          validateEmail(email)
        ); 
      };  
    const handleSubmit = (event) => {
        event.preventDefault();
        alert("Thank you to submit")
        clearForm();
    }
    return (
        <form className="formbox" onSubmit={handleSubmit}>
            <h1>Any question? Send us a message</h1>
            <fieldset>
                <FormControl>
                <FormLabel>Name:</FormLabel>
                <Input type="text" placeholder="name" value={name} onChange={(e) => setName(e.target.value)}></Input>
                </FormControl>

                <FormControl>
  <FormLabel>Email address</FormLabel>
  <Input type='email' />
  <FormHelperText>We'll never share your email.</FormHelperText>
</FormControl>

                <div>
                <label>Comment:</label>
                <textarea type="text" placeholder="Leave your comment..." value={comment} onChange={(e) => setComment(e.target.value)}></textarea>
                </div>
                <button type="submit" disabled={!getIsFormValid()}>Submit</button>
            </fieldset>
        </form>
    )

};

  export default Form;
